import React, { useState, useEffect } from "react";
import "../css/ImagesDisplayer.css";

const ImagesDisplayer = ({ imagesArray }) => {
  const [images, setImages] = useState(imagesArray);
  const [imageClassName, setImageClassName] = useState('single-image');
  const [divClassName, setDivClassName] = useState('single-image-container')

  useEffect(() => {
    console.log(imagesArray);
  }, []);

  const handleImageHover = (event) => {
    setImageClassName('single-image-open');
  };

  return (
    <div id="images-displayer-main-container">
      {images &&
        images.map((image) => {
          return (
            <div
              key={image.url}
              className={divClassName}
              onMouseOver={(event) => handleDivHover(event)}
              >
              <img
                className={imageClassName}
                src={image.url}
                onMouseOver={(event) => handleImageHover(event)}
              />
            </div>
          );
        })}
    </div>
  );
};

export default ImagesDisplayer;
