import React, { useState, useEffect } from 'react';
import compressor from 'compressorjs';

const DowngradedImage = ({ src }) => {
    const [downgradedSrc, setDowngradedSrc] = useState(null);

    useEffect(() => {
        const img = new Image();
        img.src = src;

        img.onload = () => {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            canvas.width = 300;
            canvas.height = (canvas.width / img.width) * img.height;
            ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

            compressor(canvas.toDataURL('image/jpeg'), {
                quality: 0.6,
                success(result) {
                    setDowngradedSrc(result);
                },
                error(err) {
                    console.log(err.message);
                },
            });
        };
        console.log(src)
    }, [src]);

    return <img src={downgradedSrc} alt="Downgraded Image" />;
};

export default DowngradedImage;
