import React, { useState, useEffect } from "react";
import "../css/ImagesDisplayer.css";

const ImagesDisplayer = ({ imagesArray }) => {
  const [images, setImages] = useState(imagesArray);
  const [divClassNames, setDivClassNames] = useState(
    images.map(() => "single-image-container")
  );

  useEffect(() => {
    console.log(imagesArray);
  }, []);

  const handleDivHover = (index) => {
    setDivClassNames((classNames) =>
      classNames.map((className, i) =>
        i === index ? "single-image-container-open" : className
      )
    );
    console.log("div hover");
  };

  const handleDivHoverLeave = (index) => {
    setDivClassNames((classNames) =>
      classNames.map((className, i) =>
        i === index ? "single-image-container" : className
      )
    );
    console.log("div hover leave");
  };

  return (
    <div id="images-displayer-main-container">
      {images &&
        images.map((image, index) => {
          return (
            <div
              key={image.url}
              className={divClassNames[index]}
              onMouseOver={() => handleDivHover(index)}
              onMouseLeave={() => handleDivHoverLeave(index)}
            >
              <img src={image.url} />
            </div>
          );
        })}
    </div>
  );
};

export default ImagesDisplayer;
