    // setCurrentImageIdx(currentImageIdx - 1)
    setImageOneIdx(imageOneIdx - 1)
    setImageTwoIdx(imageTwoIdx - 1)
    setImageThreeIdx(imageThreeIdx - 1)
  }




  return (
    <div>

    </div>
  )
}

export default ImagesDisplayer;
