import React, { useState, useEffect } from "react";
import NavBar from "../NavBar";

import '../css/Home.css'

const Home = ({ token, setToken }) => {
  const [hoveredDiv, setHoveredDiv] = useState(null);

  const handleMouseOver = (index) => {
    setHoveredDiv(index);
  }

  const handleMouseLeave = () => {
    setHoveredDiv(null);
  }

  return (
    <div className="home-container">
      <NavBar
        token={token}
        setToken={setToken}
      />
      <div className="home-main">
        <h1>Home</h1>
        <div id="banner-one">
          <div
            className="banner-one-div-one"
            onMouseOver={() => handleMouseOver(1)}
            onMouseLeave={() => handleMouseLeave()}
          >
            {hoveredDiv === 1 && <p>Text for banner one div one</p>}
          </div>
          <div
            className="banner-one-div-two"
            style={{ backgroundImage: "url('https://example.com/image-2.jpg')" }}
            onMouseOver={() => handleMouseOver(2)}
            onMouseLeave={() => handleMouseLeave()}
          >
            {hoveredDiv === 2 && <p>Text for banner one div two</p>}
          </div>
          <div
            className="banner-one-div-three"
            style={{ backgroundImage: "url('https://example.com/image-3.jpg')" }}
            onMouseOver={() => handleMouseOver(3)}
            onMouseLeave={() => handleMouseLeave()}
          >
            {hoveredDiv === 3 && <p>Text for banner one div three</p>}
          </div>
        </div>
      </div>
    </div>
  )
}

export default Home;
