import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import NParksServiceRequest from "../apis/nationalParksApi";
import NavBar from "../NavBar";
import Weather from "./Weather";
import MiniMap from "./MiniMap";
import Sun from "./Sun";
import '../css/TourDetails.css'

const TourDetails = ({token, setToken}) => {

  const { id } = useParams()
  const [data, setData] = useState(null)
  const [zoom, setZoom] = useState(115)
  const [centerPosition, setCenterPosition] = useState(null)

  useEffect(() => {
    const getData = async () => {

      const tourData = await NParksServiceRequest.getTourDetails(id)
      console.log(tourData)
      setData(tourData)

      const location = await NParksServiceRequest.getParkLatLong(tourData.park.parkCode)
      // console.log(location)

      const parsedLat = parseFloat(location.latitude)
      const parsedLon = parseFloat(location.longitude)

      setCenterPosition([42.35908295, -71.06764181])
      console.log([parsedLat, parsedLon ])
    }
    getData()
  }, [])


  return (
    <div className="park">
      <NavBar
        token={token}
        setToken={setToken}
      />
      <div className="park-main-container">



        {centerPosition && 
          <>
            <div className="weather">
              <Weather centerPosition={centerPosition} />
            </div>
            <div className="park-header-map-conatiner">
              <MiniMap
                zoom={zoom}
                centerPosition={centerPosition}
              />
            </div>
            <div className="park-map-footer">
              <Sun centerPosition={centerPosition} />
            </div>
          </>
        }

        <div className="park-header-main">
          {/* <h1>{data.fullName}  <a href={data.url} target='blank'><img className="more-info-icon-park-page" src={require('../images/more-info-icon.png')}></img></a></h1>
          <p><b>Location:</b> {data.addresses[0].city}, {data.addresses[0].stateCode}</p>
          <p><b>Designation:</b> {data.designation}</p>
          <p>{data.description}</p> */}
        </div>



      </div>




      {/* <div className="images">
        <div className="image-buttons-container">
          {currentImageIdx > 0
            ? <div className="back-arrow" onClick={decreaseCurrentImageIdx}><p>prev</p></div>
            : <div className="back-arrow-off"><p>prev</p></div>
          }
          {currentImageIdx < numImages - 1
            ? <div className="forward-arrow" onClick={increaseCurrentImageIdx}><p>next</p></div>
            : <div className="forward-arrow-off"><p>next</p></div>
          }
        </div>

        <img
          className='park-image'
          src={parkData.images[currentImageIdx].url}
        ></img>
      </div> */}

      <br></br>
      <br></br>
      <br></br>
      <br></br>

    </div>
  )
}

export default TourDetails;
