import React, {useState, useEffect} from "react";
import { Link } from 'react-router-dom';


import NavBar from "../NavBar";
import NParksServiceRequest from '../apis/nationalParksApi';

import '../css/NavBar.css'
import '../css/PhotoGallery.css'


const PhotoGallery = ({token, setToken}) => {

  const [images, setImages] = useState([])
  const [hoveredId, setHoveredId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const getImages = async () => {
    let resp = await NParksServiceRequest.getRandomImages()
    // console.log(resp)
    setImages(resp)
  }

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    // return () => window.removeEventListener("scroll", handleScroll);
    getImages()

  }, []);

  const handleScroll = () => {
    console.log('scroll')
    const scrollTop = window.scrollY;
    const windowHeight = window.innerHeight;
    const documentHeight = document.documentElement.scrollHeight;

    if (scrollTop + windowHeight <= documentHeight && !isLoading) {
      setIsLoading(true);
      getImages()
      setIsLoading(false)
    }
  };


  return (
    <div className="gallery-page-container">
      <NavBar
        token={token}
        setToken={setToken}
      />
      <div className="gallery-container">
        {images && images.map(image => {
          return(
            <div
              key={image.imageUrl}
              className="image"
              style={{
                backgroundImage: `url(${image.imageUrl})`
              }}
              onMouseEnter={() => setHoveredId(image.imageUrl)}
              onMouseLeave={() => setHoveredId(null)}
            >
              {hoveredId === image.imageUrl && (
                <div className="image-hover-div">
                  <p><b><Link
                    to={`/park/${image.parkCode}`
                    }>{image.parkName}</Link> - {image.state}</b></p>
                </div>
              )}
            </div>
          )
          }
        )}
        {isLoading && <p>Loading...</p>}
      </div>
    </div>
  )
}

export default PhotoGallery;
