import React, { useState, useEffect } from "react";
import '../css/ParksByStateBanner.css'
import StateSelect from "./StateSelect";
import { useNavigate } from 'react-router-dom';
import '../hooks/useStateNameConverter'

const ParksByState = () => {

  const [USstate, setUSstate] = useState(null)
  const navigate = useNavigate();

  const handleFingParksByState = async (e, type) => {
    if (USstate === 'placeholder') {
      // setUSstate('Pick a state to search')
    } else {
      navigate(`/visitorcenters/${USstate}`);
    }
  }

  return (
    <div id="parks-by-state-container">
      {USstate === null
        ? <h1>Pick a state to find Parks</h1>
        : <h1> See parks in {USstate}</h1>
      }
      <div id="state-select">
        <StateSelect USstate={USstate} setUSstate={setUSstate} />
      </div>
      <div
        id="search-button"
        onClick={(e) => handleFingParksByState(e)}
      >
        <p>Search</p>
      </div>
    </div>
  )
}

export default ParksByState;
