import React, { useState, useEffect } from 'react'
import { useNavigate, Link } from 'react-router-dom';


import { MapContainer, Marker, Popup, TileLayer } from 'react-leaflet'

import '../css/MiniMap.css'

const MiniMap = ({ zoom, centerPosition, campsites }) => {


  return (
    <>
      <MapContainer
        key={'miniMapContainer'}
        center={centerPosition}
        scrollWheelZoom={false}
        zoom={zoom}>

        {centerPosition && centerPosition.map(
          park => {
            const rand = Math.random()
            return (
              <Marker
                key={rand}
                position={[
                  centerPosition[0],
                  centerPosition[1]
                ]}
              ></Marker>
        )})}

        {/* ######################################## */}




        {/* {showingParks &&
          showingParks.map(park => {
            return (
              <Marker
                key={park.parkCode}
                position={[
                  park.latitude,
                  park.longitude
                ]}
              >
                <Popup>
                  <h4>{park.fullName}</h4>


                  <div className='popup-image-div'>
                    <img
                      className='popup-image'
                      src={park.images[0].url}
                    ></img>
                  </div>


                  <div className='popup-description-div'>
                    <p><Link
                      className='link-info'
                      to={`/park/${park.parkCode}`
                      }>View Park
                    </Link></p>
                    <p className='popup-description'>{park.description}</p>
                  </div>
                </Popup>
              </Marker>
            )
          })
        } */}






        {/* ######################################## */}

        <TileLayer
          key={'tileLayer'}
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
      </MapContainer>
    </>
  )
}


export default MiniMap;
