import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import NParksServiceRequest from "../apis/nationalParksApi";
import NavBar from "../NavBar";
import '../css/PeopleSearchResults.css'

const PeopleSearchResults = ({ token, setToken }) => {

  const { searchTerm } = useParams()
  const [numResults, setNumResults] = useState(10)
  const [results, setResults] = useState(null)

  useEffect(() => {
    const doSearch = async () => {
      const response = await NParksServiceRequest.searchInPeople(searchTerm, numResults)
      // console.log(searchTerm)
      console.log(response)
      setResults(response)
    }
    doSearch()
  }, [numResults])

  const increaseNumberOfSearchResults = () => {
    console.log('click')
    setNumResults(numResults + 10)
  }

  function parseHTML(html) {
    html = html.replace(/<img.*?\/>/g, '');
    html = html.replace(/<br \/>\s*<span.*?<\/span>/g, '');
    html = html.replace(/<a.*?>.*?<\/a>/g, '');
    html = html.replace(/<.*?>/g, (match) => {
      const content = match.replace(/<\/?[^>]+>/g, '');
      return `<p>${content}</p>`;
    });
    html = html.substring(0, 800);
    return (
      <div dangerouslySetInnerHTML={{ __html: html }}></div>
    );
  }

  return (
    <div id="people-search-results-main-container">
      <NavBar
        token={token}
        setToken={setToken}
      />
      <h1>Search Results</h1>
      <div id="people-search-results-items-container">
        {results && results.map(item => {
          return (
            <div key={item.id} className="people-search-results-item-container">
            {item.images[0].url === ''
                ? <div className='people-search-result-image'>
                    <img src={require('../images/black.png')}></img>
                  </div>
                : <div className='people-search-result-image'>
                    <img src={item.images[0].url}></img>
                  </div>
            }
            {/* <div className="poeple-search-results-info">
              <p>{item.firstName} {item.lastName}</p>
              {parseHTML(item.bodyText)}
              {item.url
                ? <a href={item.url} target="_blank">
                  <img className="people-more-info-icon" src={require('../images/more-info-icon.png')}></img>
                </a>
                : <></>
              }
            </div> */}
            </div>
          )
        })}
        <button className="people-load-more-search-results" onClick={increaseNumberOfSearchResults}>Get More Results</button>
      </div>
    </div>
  )
}

export default PeopleSearchResults;
