import React, { useState, useEffect } from "react";
import '../css/ParksByStateBanner.css'
import StateSelect from "./StateSelect";
import { useNavigate } from 'react-router-dom';
import useStateNameConverter from "../hooks/useStateNameConverter";
import NParksServiceRequest from "../apis/nationalParksApi";


const ParksByStateBanner = () => {

  const [USstate, setUSstate] = useState(null)
  const [stateName, convertStateName] = useStateNameConverter();
  const [containerSize, setContainerSize] = useState("parks-by-state-container-closed")
  const [itemsContainerSize, setItemsContainerSize] = useState("parks-by-state-items-container-closed")
  const [parksList, setParksList] = useState(null)
  const navigate = useNavigate();

  useEffect(() => {
    convertStateName(USstate)
  }, [USstate])

  const handleFingParksByState = async (e) => {
    if (USstate === null) {
      console.log('null')
    } else {
      const response = await NParksServiceRequest.getParksByState(USstate)
      // console.log(response)
      setContainerSize('parks-by-state-container-open')
      setItemsContainerSize('parks-by-state-items-container-open')
      setParksList(response)
    }
  }

  return (
    <div id={containerSize}>
      {USstate === null
        ? <h1>Pick a state to find Parks</h1>
        : <h1> See Parks in {stateName}</h1>
      }
      <div id="state-select">
        <StateSelect USstate={USstate} setUSstate={setUSstate} />
      </div>
      <div
        id="search-button"
        onClick={(e) => handleFingParksByState(e)}
      >
        <p>Search</p>
      </div>
      <div key='search-banner-container' id={itemsContainerSize}>
        {parksList && parksList.map(item => {
          return (
            <div className="search-parks-by-state-banner-item">
              <p>{item.id}</p>
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default ParksByStateBanner;
