import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

import BackendApiRequest from "../apis/backendApi";
import useLocalStorage from "../hooks/useLocalStorage";
import NavBar from "../NavBar";

import '../css/RegisterForm.css'

const RegisterForm = ({token, setToken}) => {

  const [localStoreToken, localRemoveToken, localRetrieveToken, localDecodeToken] = useLocalStorage()

  const INITIAL_STATE = {
    'username': '',
    'password': '',
    'firstName': '',
    'lastName': '',
    'email': '',
  }

  const [formData, setFormData] = useState(INITIAL_STATE)
  const navigate = useNavigate();

  const handleChange = evt => {
    const { name, value } = evt.target;
    setFormData(data => ({
      ...data,
      [name]: value
    }));
  };

  const handleSubmit = async (evt) => {
    evt.preventDefault();
    const request = await BackendApiRequest.registerNewUser(formData)
    console.log(request)
    const token = request.data.token
    setToken(token)
    localStoreToken(token)
    setFormData(INITIAL_STATE)
    navigate('/')
  }


  return (
    <div id="register-form-main-container">
      <NavBar
        token={token}
        setToken={setToken}
      />
      <div id="form-container">
        <form onSubmit={handleSubmit}>

          <label htmlFor="username" className="col-md-6">Username: </label>
          <input
            required
            id={formData.username}
            type="text"
            name="username"
            placeholder="username"
            value={formData.username}
            onChange={handleChange}
            className="col-md-6"
          /><br></br><br></br>

          <label htmlFor="password" className="col-md-6">Password: </label>
          <input
            required
            id={formData.password}
            type="text"
            name="password"
            placeholder="password"
            value={formData.password}
            onChange={handleChange}
            className="col-md-6"
          /><br></br><br></br>

          <label htmlFor="firstName" className="col-md-6">First Name: </label>
          <input
            required
            id={formData.firstName}
            type="text"
            name="firstName"
            placeholder="first name"
            value={formData.firstName}
            onChange={handleChange}
            className="col-md-6"
          /><br></br><br></br>

          <label htmlFor="lastName" className="col-md-6">Last Name: </label>
          <input
            required
            id={formData.lastName}
            type="text"
            name="lastName"
            placeholder="last name"
            value={formData.lastName}
            onChange={handleChange}
            className="col-md-6"
          /><br></br><br></br>

          <label htmlFor="email" className="col-md-6">email: </label>
          <input
            required
            id={formData.email}
            type="text"
            name="email"
            placeholder="email"
            value={formData.email}
            onChange={handleChange}
            className="col-md-6"
          /><br></br><br></br>

          <button id="submit-button">Submit</button>
        </form>
      </div>
    </div>
  )
}

export default RegisterForm;
