import React, { useEffect, useState } from 'react';
import useLocalStorage from "../hooks/useLocalStorage";
import BackendApiRequest from "../apis/backendApi";

import '../css/AddToTripDropdown.css'

const AddToTripDropdown = ({ dropDownOptions, dropDownOnSelect }) => {

  const [localStoreToken, localRemoveToken, localRetrieveToken, localDecodeToken] = useLocalStorage()
  const [selectedOption, setSelectedOption] = useState(null);
  const [username, setUsername] = useState(null)
  const [trips, setTrips] = useState([])

  useEffect(() => {
    const getUsername = async () => {
      const token = await localDecodeToken()
      setUsername(token.username)
    }
    getUsername()
  }, [])

  useEffect(() => {
    const getTrips = async () => {
      const data = await BackendApiRequest.getUserTrips(username)
      console.log(data)
      setTrips(data)
      setSelectedOption(data)
    }
    getTrips()
  }, [username])

  const handleSelect = (option) => {
    setSelectedOption(option);
    dropDownOnSelect(option);
  };

  return (
    <div className="dropdown">
      <button className="dropdown-toggle" type="button" data-toggle="dropdown">
        {selectedOption || 'Add to a trip itinerary'}
      </button>
      {/* {trips.length > 0 &&
        <div className="dropdown-menu">
          {dropDownOptions.map((option) => (
            <button className="dropdown-item" key={option} onClick={() => handleSelect(option)}>
              {option}
            </button>
          ))}
        </div>
      } */}

    </div>
  );
};

export default AddToTripDropdown;
