import axios from 'axios';

class RecreationAPI {
  constructor(apiKey) {
    this.apiKey = apiKey;
    this.baseUrl = "https://ridb.recreation.gov/api/v1";
  }

  async createReservation(activityId, startDate, endDate, numberOfPeople, payment) {
    const url = `${this.baseUrl}/reservation`;
    const headers = { "Content-Type": "application/json", "apikey": this.apiKey };
    const body = {
      "ActivityID": activityId,
      "StartDate": startDate,
      "EndDate": endDate,
      "NumberOfPeople": numberOfPeople,
      "Payment": payment
    };
    const response = await axios.post(url, body, { headers });
    return response.data;
  }
}

export default RecreationAPI;
