import React, { useState, useEffect } from "react";
import "../css/ImagesDisplayer.css";

const ImagesDisplayer = ({ imagesArray }) => {
  const [images, setImages] = useState(imagesArray);
  const [hoveredIndex, setHoveredIndex] = useState(-1);

  useEffect(() => {
    console.log(imagesArray);
  }, []);

  const handleDivHover = (index) => {
    setHoveredIndex(index);
    console.log('div hover')
  };

  const handleDivHoverLeave = (event) => {
    setHoveredIndex(-1);
    console.log('div hover leave')
  }

  return (
    <div id="images-displayer-main-container">
      {images &&
        images.map((image, index) => {
          return (
            <div
              key={image.url}
              className={index === hoveredIndex ? 'single-image-container-open' : 'single-image-container'}
              onMouseOver={() => handleDivHover(index)}
              onMouseLeave={(event) => handleDivHoverLeave(event)}
            >
              <img src={image.url} />
            </div>
          );
        })}
    </div>
  );
};

export default ImagesDisplayer;
