import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import MarkerClusterGroup from 'react-leaflet-markercluster';

import '../css/MainMap.css';

const MainMap = ({ showingParks }) => {
  const [zoom, setZoom] = useState(4);
  const [centerPosition, setCenterPosition] = useState([35, -94]);

  return (
    <div className='map-container'>
      <MapContainer
        key={'MapContainer'}
        center={centerPosition}
        zoom={zoom}>

        <TileLayer
          key={'tileLayer'}
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {showingParks && (
          <MarkerClusterGroup>
            {showingParks.map((park) => (
              <Marker
                key={park.parkCode}
                position={[
                  park.latitude,
                  park.longitude,
                ]}
              >
                <Popup>
                  <h4>
                    {park.fullName} -{' '}
                    <Link to={`/park/${park.parkCode}`}>More Info</Link>
                  </h4>

                  <div className='popup-image-div'>
                    <img
                      className='popup-image'
                      src={park.images[0].url}
                    />
                  </div>

                  <div className='popup-description-div'>
                    <p className='popup-description'>{park.description}</p>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MarkerClusterGroup>
        )}

      </MapContainer>
    </div>
  );
};

export default MainMap;
