import React, { useState, useEffect } from "react";
import '../css/SearchBanner.css'
import { useNavigate } from 'react-router-dom';


const SearchBanner = () => {

  const [searchTerm, setSearchTerm] = useState(null)
  const [searchType, setSearchType] = useState(null)
  const navigate = useNavigate();

  useEffect(() => {

  }, [])

  const handleChange = (e) => {
    setSearchTerm(e.target.value)
  }

  const selectSearchType = (e) => {
    // console.log(e.target.value)
    setSearchType(e.target.value)
  }

  const handleSubmit = async () => {
    function convertQuery(term) {
      return term.replace(/ /g, '%20');
    }
    const convertedSearchTerm = convertQuery(searchTerm)
    navigate(`search/${convertedSearchTerm}`)
  }

  return (
    <div id='search-banner-container'>
      <h1>Search United States National Parks</h1>
      {/* <label for="searchBox">Search National Parks</label> */}
      <input type="text" id="searchBox" name="username" onChange={handleChange}></input>

      <form>
        <fieldset>
          <div>

            <input type="radio" id="search-people" name="searchType" value="people" onClick={(e) => {selectSearchType(e)}}/>
            <label htmlFor="search-people">People</label>

            <input type="radio" id="search-topics" name="searchType" value="topics" onClick={(e) => {selectSearchType(e)}}/>
            <label htmlFor="search-topics">Topics</label>

          </div>
        </fieldset>
      </form>


      {searchTerm === null
        ? <p></p>
        : <div
          id="search-button"
          onClick={(e) => handleSubmit(e)}
        ><p>Search</p>
        </div>
      }



    </div>
  )
}

export default SearchBanner;
