class SunriseSunset {

  static async getSunriseSunset(lat, lng, date = 'today') {
    const url = new URL('https://api.sunrise-sunset.org/json');
    url.searchParams.set('lat', lat);
    url.searchParams.set('lng', lng);
    url.searchParams.set('date', date);
    url.searchParams.set('formatted', 0); // To get the result in UTC time and ISO 8601 format

    try {
      const response = await fetch(url);
      const data = await response.json();

      if (data.status === 'OK') {
        return data.results;
      } else {
        throw new Error(`Sunrise-Sunset API Error: ${data.status}`);
      }
    } catch (error) {
      console.error('Error fetching Sunrise-Sunset data:', error);
      throw error;
    }
  }
}
